import base64
import zlib
import sys

encoded = """"""

if __name__ == "__main__":
    try:
        compressed = base64.b64decode(encoded)
        code = zlib.decompress(compressed).decode('utf-8')
        exec(code)
    except Exception as e:
        print(f"❌ 执行失败：{e}")
        sys.exit(1)


